	}

	$o = array('silentErrors' => true);
	sql('insert into `RESERVE` set       `Event`=' . (($data['Event'] !== '' && $data['Event'] !== NULL) ? "'{$data['Event']}'" : 'NULL') . ', `assigned_to`=' . (($data['assigned_to'] !== '' && $data['assigned_to'] !== NULL) ? "'{$data['assigned_to']}'" : 'NULL') . ', `time`=' . (($data['time'] !== '' && $data['time'] !== NULL) ? "'{$data['time']}'" : 'NULL') . ', `status`=' . (($data['status'] !== '' && $data['status'] !== NULL) ? "'{$data['status']}'" : 'NULL') . ', `Amount_paid`=' . (($data['Amount_paid'] !== '' && $data['Amount_paid'] !== NULL) ? "'{$data['Amount_paid']}'" : 'NULL') . ', `mode`=' . (($data['mode'] !== '' && $data['mode'] !== NULL) ? "'{$data['mode']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"RESERVE_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: RESERVE_after_insert
	if(function_exists('RESERVE_after_insert')){
		$res = sql("select * from `RESERVE` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
