	$templateCode = str_replace('<%%COMBOTEXT(mode)%%>', $combo_mode->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'Event' => array('EVENTS', 'Event'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Event)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(assigned_to)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(time)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(status)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Amount_paid)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(mode)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Event)%%>', safe_html($urow['Event']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Event)%%>', html_attr($row['Event']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Event)%%>', urlencode($urow['Event']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(assigned_to)%%>', safe_html($urow['assigned_to']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(assigned_to)%%>', html_attr($row['assigned_to']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(assigned_to)%%>', urlencode($urow['assigned_to']), $templateCode);
		$templateCode = str_replace('<%%VALUE(time)%%>', time12(html_attr($row['time'])), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time)%%>', urlencode(time12($urow['time'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', safe_html($urow['status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', html_attr($row['status']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode($urow['status']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Amount_paid)%%>', safe_html($urow['Amount_paid']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Amount_paid)%%>', html_attr($row['Amount_paid']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Amount_paid)%%>', urlencode($urow['Amount_paid']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(mode)%%>', safe_html($urow['mode']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(mode)%%>', html_attr($row['mode']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(mode)%%>', urlencode($urow['mode']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Event)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Event)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(assigned_to)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(assigned_to)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(time)%%>', time12(''), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time)%%>', urlencode(time12('')), $templateCode);
		$templateCode = str_replace('<%%VALUE(status)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Amount_paid)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Amount_paid)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(mode)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(mode)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('RESERVE');
	if($selected_id){
		$jdata = get_joined_record('RESERVE', $selected_id);
		if($jdata === false) $jdata = get_defaults('RESERVE');
		$rdata = $row;
	}
	$templateCode .= loadView('RESERVE-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: RESERVE_dv
	if(function_exists('RESERVE_dv')){
		$args=array();
		RESERVE_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>